function formReduce(prevState, action) {

  if (action.type === 'USER_INPUT') {
    return {
      valueEmail: action.emailValue,
      isEmailValid: action.emailValue.includes('@'),
    }
  }
  if (action.action === 'INPUT_BLUR') {
    return {
      valueEmail: prevState.emailValue,
      isEmailValid: prevState.emailValue.includes('@'),
    }
  }

  if (action.type === 'PASSWORD_INPUT') {
    return {
      valuePassword: action.passwordValue,
      isPasswordValid: action.passwordValue.trim().length > 6,
    }
  }
  if (action.action === 'PASSWORD_BLUR') {
    return {
      valuePassword: prevState.passwordValue,
      isPasswordValid: prevState.passwordValue.trim().length > 6,
    }
  }


  return {
    valueEmail: prevState.emailValue,
    isEmailValid: prevState.isEmailValue,
    valuePassword: prevState.passwordValue,
    isPasswordValid: prevState.isPasswordValue
  }

}

const Login = (props) => {

  const [formState, dispatchForm] = useReducer(formReduce, {
    valueEmail: '',
    isEmailValid: '',
    valuePassword: '',
    isPasswordValid: '',
  })
  const [formIsValid, setFormIsValid] = useState(false)

  useEffect(() => {
    const timer = setTimeout(() => {
      setFormIsValid(formState.valueEmail.includes('@')&& enteredPassword.trim().length > 6)
    }, 500)
