function formReduce(prevState, action) {
  if (action.type === 'PASSWORD_VALUE') {
    return {
      passwordValue: action.passwordValue,
      isPasswordValue: action.passwordValue,
    }
  }
  if (action.action === 'PASSWORD_ONBLUR') {
    return {
      passwordValue: prevState.passwordValue,
      isPasswordValue: prevState.passwordValue,
    }
  }
  if (action.type === 'EMAIL_VALUE') {
    return {
      emailValue: action.emailValue,
      isEmailValue: action.emailValue.includes('@'),
    }
  }
  if (action.action === 'INPUT_ONBLUR') {
    return {
      emailValue: prevState.emailValue,
      isEmailValue: prevState.emailValue.includes('@'),
    }
  }
  if(action.type==="IS_FORM_VALID"){
    return{
      isFormValid:action.isFormValid.includes('@')
    }
  }
  return {
    ...prevState,
  }
}

const Login = (props) => {

  const [formState, dispatchForm] = useReducer(formReduce, {
    emailValue: '',
    isEmailValue: false,
    passwordValue: '',
    isPasswordValue: false,
    isFormValid: false,
  })
  const [formIsValid, setFormIsValid] = useState(false)

  useEffect(() => {
    const timer = setTimeout(() => {
      setFormIsValid(formState.emailValue.includes('@'))
    }, 500)
